var msgP = {};
var msgS = {};
var msgQ = {};

msgP.payload = msg.payload.data["Power Low"];
msgP.topic = 'P';

msgS.payload = msg.S;
msgS.topic = 'S';

msgQ.payload = msg.Q;
msgQ.topic = 'Q'

return [msgS, msgP, msgQ];

if (msg.payload.timestamp == global.energyTimestamp){
    msg.payload = false;
}
else{
    msg.payload = true;
}

global.energyTimestamp = msg.payload.timestamp;


return msg;

if(msg.payload.SliderPosition == "Left"){
    msg.payload = 0;
}
else if (msg.payload.SliderPosition == "Right"){
    msg.payload = 1;
}

return msg;

if (msg.payload.LightLeft == "OFF"){
    msg.payload = false;
}
else if (msg.payload.LightLeft == "ON"){
    msg.payload = true;
}

return msg;

if (msg.payload.LightRight == "OFF"){
    msg.payload = false;
}
else if (msg.payload.LightRight == "ON"){
    msg.payload = true;
}

return msg;

if (msg.payload.Timestamp != null){
    msg.payload = true;
}
else{
    msg.payload = false;
}

return msg;

if(msg.payload.LatchSwitch == false){
    msg.payload = "Open";
}
else if (msg.payload.LatchSwitch == true){
    msg.payload = "Closed";
}
return msg;

if (msg.payload.data["Current Low"] >= 4 && !global.get("coffeestarted")){
    //coffee started producing
    global.set("coffeestarted", true);
}
if (msg.payload.data["Current Low"] <= 0.04 && global.get("coffeestarted")){
    //stopped producing
    global.set("coffeestarted",false);
    global.set("coffeespulled", global.get("coffeespulled") + 1);
}

msg.coffeespulled = global.get("coffeespulled");
msg.coffeestarted = global.get("coffeestarted");

return msg;

